mapper.select:
  		SELECT

	  	FROM dt_orders odr
	  	WHERE odr.${colname} = #{value}

mapper.insert:
INSERT INTO dt_orders(
	product_ref
	, order_time
	, payment_due_time
	, paid_time
	, total_amount
	, customer_ref
	, confirmer_ref
	, order_uuid
	, order_state
	, pg_vendor
) values (
	#{productRef}
	, #{orderTime}
	, #{paymentDueTime}
	, null
	, #{totalAmount}
	, #{customerRef}
	, null
	, #{orderUuid}
	, #{orderState}
	, #{paygateVendor}
)

mapper.update:
UPDATE dt_orders
SET
	paid_time = #{paidTime}
  , confirmer_ref = #{confirmerRef}
  , order_state = #{orderState}
  , pg_vendor = #{paygateVendor}
  , trx_detail = #{transactionDetail}
  , mid_trx_uid = #{midTransactionUid}
  , end_trx_uid = #{endTransactionUid}
WHERE
	odr_seq = #{seq}
	AND
	paid_time IS NULL
	AND
	confirmer_ref IS NULL

UPDATE dt_orders
SET
	order_state = #{orderState}
WHERE
	order_uuid = #{orderUuid}
