mapper.select:
  	SELECT 

  		, 
	FROM dt_exam_papers  dep 
	JOIN dt_exam_answers ans
	  ON dep.exam_seq = ans.exam_ref 
	WHERE dep.student_ref = #{student}
	      AND
	      dep.section_ref = #{section}
	      AND
	      dep.type = #{type};

mapper.insert:
  		INSERT INTO dt_exam_papers (
 			  section_ref
 			, student_ref
 			, student_age_month
 			, `type`
 			, mode
 			, question_offset
 			, num_of_questions
 			, start_time
 			, end_time
		) VALUES (
			  #{sectionRef}
			, #{studentRef}
			, #{ageInMonth}
			, #{type}
			, #{mode}
			, #{questionOffset}
			, #{numOfQuestions}
			, #{startTime}
			, #{endTime} 
		);
