mapper.insert:
INSERT INTO  dt_order_forms (
	org_name
  , sender_name
  , sender_contact_info
  , sender_email
  , org_email
  , org_contact_info
  , content
  , group_order_uuid
  , creation_time
  , group_order_state
  , sender_ref
) VALUES (
	#{orgName}
  , #{senderName}
  , #{senderContactInfo}
  , #{senderEmail}
  , #{orgEmail}
  , #{orgContactInfo}
  , #{content}
  , #{orderUuid}
  , #{creationTime}
  , #{state}
  , #{senderRef}
)

mapper.update:
  	UPDATE dt_order_forms 
	SET
		group_order_state = #{state}
	WHERE seq = #{seq}
