mapper.select:
SELECT

FROOM dt_deliveries dlv
WHERE dlv.dl_order_ref = #{orderRef}

mapper.insert:
INSERT INTO dt_deliveries(
	dl_base_address
	, dl_detail_address
	, dl_receiver_name
	, dl_zipcode
	, dl_order_ref
	, dl_user_ref
) VALUES(
	#{baseAddress}
	, #{detailAddress}
	, #{receiverName}
	, #{zipCode}
	, #{orderRef}
	, #{userRef}
)
